mapper.update:
UPDATE USER_TABLE
SET USER_NAME = #{userNm} , USER_RANK = #{userRk}
WHERE USER_ID = #{userId}

UPDATE USER_TABLE
    SET USER_COUNT = 0
    WHERE USER_ID = #{userId}

UPDATE USER_TABLE
    SET USER_ACTIVATION = 'N'
    WHERE USER_ID = #{userId}

UPDATE USER_TABLE
    SET USER_COUNT = #{plusCount}
    WHERE USER_ID = #{userId}

mapper.select:

SELECT COUNT(*)
FROM USER_TABLE
WHERE USER_ID = #{UserId}

SELECT COUNT(*)
FROM USER_TABLE
WHERE USER_NAME = #{UserName}
